import { Component, Input, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AuthService } from 'src/app/shared/services/auth.service';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  @Input({required: true}) userImg: string = '';
  username = JSON.parse(sessionStorage.getItem('loggedInUser')!).name;
  auth = inject(AuthService);
  navList = ["Home", "TV Shows", "News & Popular", "My List", "Browse by Language"]
}
<header class="h-[68px] z-10 bg-gray-900 sticky top-0 flex items-center justify-between px-10">
  <!-- Logo + navitems -->
  <div class="flex gap-5">
    <img width="110" src="/assets/movie.jpg" alt="netflix-logo">
    <ul class="flex items-center" *ngFor="let item of navList">
      <li class="text-neutral-200 cursor-pointer text-sm font-extralight hover:text-gray-400 transition-[1s]">{{item}}</li>
    </ul>
  </div>
  <!-- Welcome User | pofile Img | Singout -->
  <div class="text-white flex items-center gap-4">
    <div class="flex items-center gap-2">
      <a href="/"><span>Welcome <strong>{{username}}</strong></span></a>
      <img [src]="userImg" width="42" alt="user-img">
    </div>
    &nbsp; | &nbsp;
    <a (click)="auth.signOut()">Signout</a>
  </div>
</header>
